import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import Image from 'next/image'

import Button from '@components/Button'
import { getAccount } from '@selectors/user.selectors'
import { getChainId } from '@selectors/global.selectors'
import {
  openConnectMetamaskModal,
  openSignupModal,
  openCryptoOptionsModal,
  openSwitchNetworkModal
} from '@actions/modals.actions'
import { getUser } from '@helpers/user.helpers'
import {
  POLYGON_CHAINID
} from '@constants/global.constants'

import { gsap } from "gsap"
import { ScrollTrigger } from "gsap/dist/ScrollTrigger"

import styles from './styles.module.scss'
import MintSection from '@components/homepage/MintSection'

function getWindowDimensions() {
  const { innerWidth: width, innerHeight: height } = window
  return {
    width,
    height,
  }
}

function useWindowDimensions() {
  const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions())

  useEffect(() => {
    function handleResize() {
      setWindowDimensions(getWindowDimensions())
    }

    window.addEventListener('resize', handleResize)
    return () => window.removeEventListener('resize', handleResize)
  }, [])

  return windowDimensions
}

function Landing(props) {
  const screenWidth = useWindowDimensions().width
  const [isMobile, setIsMobile] = useState(false)
  const account = useSelector(getAccount)
  const chainId = useSelector(getChainId)
  const user = useSelector(getUser)
  const dispatch = useDispatch()

  useEffect(() => {
    screenWidth > 707 ? setIsMobile(false) : setIsMobile(true)
  }, [screenWidth])

  useEffect(() => {
    if (typeof window !== "undefined") {
      console.log('here')
      gsap.registerPlugin(ScrollTrigger);
    }
  }, []);


  return (
    <>
      {
      // !isMobile ? 
      (
        <div className={styles.wrapper}>
          <div className={styles.background}>
            <img src='/images/homepage/background.png' />
          </div>
          <section className={styles.mintSection}>
            <MintSection />
          </section>
          <section className={styles.aboutSection}></section>
          <section className={styles.collectionSection}></section>
          <section className={styles.shopSection}></section>
        </div>
      )
      }
    </>
  )
}

export default Landing
